    private void jButtonIgActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonIgActionPerformed
        y = Integer.parseInt(jLabel1.getText());
        if(k==1){
            int res = x + y;
            jLabel1.setText(""+res);
        }
        if(k==2){
            int res = x - y;
            jLabel1.setText(""+res);
        }
        if(k==3){
            int res = x*y;
            jLabel1.setText(""+res);
        }
        if(k==4){
            int res = x/y;
            jLabel1.setText(""+res);
        }
    }//GEN-LAST:event_jButtonIgActionPerformed

